import {useState} from 'react';
import "./Expense.css";
import Card from "../UI/Card";
import ExpenseItem from './ExpenseItem';
import ExpenseFilter from './ExpenseFilter';
import ExpenseList from './ExpenseList';

const Expense = (props) => {

    const [selectedYear, setYear] = useState('2021');

    let filteredExpenses = <p>No Expenses Found.</p>;


    const changeYearHandler = (yearVal) => {
        setYear(yearVal);
        console.log(yearVal);

        filteredExpenses = (props.expenses.filter((exp) => exp.date.getFullYear().toString() === yearVal));
        console.log(filteredExpenses);
    }


      return (<Card className="expense">
        <ExpenseFilter selectedYear={selectedYear} onChangeYear={changeYearHandler}/>
        <ExpenseList filteredExpenses={filteredExpenses}/>
      </Card>);
}


export default Expense;
